const CpfOperationService = require('./cpf-operation-service'),
	CpfConfig = require('../config/cpf-engine-config.js'),
	CpfMessage = require('./../cpf/cpf-message'),
	CpfDocument = require('../cpf/request/platform/cpf-document.js');

class SplitPDFService extends CpfOperationService {
	constructor() {
		super();
	}

	getCPFMessage(operationMessage) {
		let outputDocument = new CpfDocument('text/directory','file'),
			params = (operationMessage.options) ? Object.assign({}, {
				fileCount: operationMessage.options.fileCount === null ? undefined : operationMessage.options.fileCount,
				pageCount: operationMessage.options.pageCount === null ? undefined : operationMessage.options.pageCount,
				pageRanges: operationMessage.options.pageRanges === null ? undefined : operationMessage.options.pageRanges.getRanges()
			} ) : {},
			inputDocuments = new CpfDocument(operationMessage.sourceFileRefs.input.mediaType,'file0'),
			files = [operationMessage.sourceFileRefs];
		return new CpfMessage(inputDocuments, [outputDocument], params, files, CpfConfig.splitPdf);
	}

}

module.exports = SplitPDFService;

