const JwtAuthenticator = require('./jwt-authenticator'),
	ServiceAccountCredentials = require('../../auth/service-account-credentials'),
	ServiceTokenCredentials = require('./service-token-credentials'),
	ServiceTokenAuthenticator = require('./service-token-authenticator');

class AuthenticatorFactory {

	static getAuthenticator(credentials) {
		if (credentials instanceof ServiceAccountCredentials){
			return new JwtAuthenticator(credentials);
		} else if(credentials instanceof ServiceTokenCredentials){
			return new ServiceTokenAuthenticator(credentials);
		}
		else {
			throw new Error("Invalid Credentials provided as argument");
		}
	}
}


module.exports = AuthenticatorFactory;
