const ServiceAccountCredentials = require('../../auth/service-account-credentials'),
	DefaultConfig = require('../../internal/config/dc-services-default-config');


class ServiceAccountCredentialsWithUri extends ServiceAccountCredentials{

	constructor(ims_base_uri, client_id, client_secret, private_key, claim, organization_id, account_id,opsCreateUri) {
		super(client_id,client_secret,private_key,organization_id,account_id)
		this.imsBaseUri = ims_base_uri || DefaultConfig.imsBaseUri;
		this.claim = claim || (this.imsBaseUri + DefaultConfig.claim);
		this.opsCreateUri = opsCreateUri;
		this.validate();    											//parent (ServiceAccountCredentials) class validate method is called
	}

	getClaim() {
		return this.claim;
	}

	getImsBaseUri() {
		return this.imsBaseUri;
	}

	getOpsCreateUri() {
		return this.opsCreateUri;
	}
}

module.exports = ServiceAccountCredentialsWithUri
