class CPFMessage {
	constructor(inputDocuments, outputDocument, params, files, cpfEngineConfig, extractOutput){
		this.inputDocuments = inputDocuments;
		this.outputDocument = outputDocument;
		this.params = params;
		this.cpfEngineConfig = cpfEngineConfig;
		this.files = files;
		this.extractOutputInfo = extractOutput;
		this.inputUrl = null;
	}

	setInputUrl(inputUrl){
		this.inputUrl = inputUrl;
	}
}

module.exports = CPFMessage;
