const CreatePDFFromWordOptions = require('./../../../../operation/option/createpdf/create-pdf-from-word-options'),
	CreatePDFFromExcelOptions = require('./../../../../operation/option/createpdf/create-pdf-from-excel-options'),
	CreatePDFFromPPTOptions = require('./../../../../operation/option/createpdf/create-pdf-from-ppt-options');

class CreatePdfOptions {

	constructor(options) {

		var optionType = "";

		if (options instanceof CreatePDFFromWordOptions) {
			optionType = "wordFormatOptions";
		} else if (options instanceof CreatePDFFromExcelOptions) {
			optionType = "excelFormatOptions";
		} else if (options instanceof CreatePDFFromPPTOptions) {
			optionType = "pptFormatOptions";
		}
		if(optionType) {
			this[optionType] = {
				documentLanguage: options.documentLanguage,
				createTaggedPDF: options.createTaggedPDF
			}
		}
	}
}

module.exports = CreatePdfOptions;
